import React, { useEffect, useState, useContext } from 'react';
import { getDistance } from 'geolib';
import { AppContext } from './App.js'
import axios from 'axios';
import App from './App.js'
import './App.css';

const Route = (props) => {

    const [currentLocation, setCurrentLocation] = useState(null)
    const [stationList, setStationList] = useState([])
    const [currentRoute, setCurrentRoute] = useState([])

    const [startStation, setStartStation] = useState(null)
    const [endStation, setEndStation] = useState(null)
    const [startStationName, setStartStationName] = useState(null)
    const [endStationName, setEndStationName] = useState(null)


    const tempCurrLoc = {latitude: 37.421552399999996, longitude: -122.09509510000001}
    const tempDestLoc = {latitude: 38.324703, longitude: -77.908226}


    useEffect(() => {
        setStationList(props.stationList)
      }, [props.stationList])

    useEffect(() => {
        setCurrentLocation(props.currentLocation)
    }, [props.currentLocation])




    function findNearestStation(inputLocation){
        var minDistance = Infinity
        var minStationCode = null
        props.stationList.map((station) => {
            var currDist = getDistance(
                                {latitude: station.Lat, longitude: station.Lon},
                                inputLocation) * 0.000621371
            if (currDist < minDistance){
                minDistance = currDist
                minStationCode = station.Code
            }
        })

        return minDistance, minStationCode
    }




    function fetchRouteData(start, end) {

        console.log('fetching route data', start, end)
        if (start && end){
            axios.get(`${process.env.REACT_APP_ROUTE_API}/FromStationCode=${start}&ToStationCode=${end}&api_key=${process.env.REACT_APP_SECRET}`)
                .then((data) => setCurrentRoute(data.data))
        }
    }

    const handleDestinationEnter = () => {
        var startDelta, startStation = findNearestStation(currentLocation)
        var endDelta, endStation = findNearestStation(tempDestLoc)
        console.log(startStation, endStation)
        fetchRouteData(startStation, endStation)
    }

    if (currentRoute){
        console.log(currentRoute)
    }



    return(
        <div>
            <div className='column spacer'>

                <div>Where are you going?</div>
                <div className='button spacer' onClick={handleDestinationEnter}>entered destination</div>




            </div>           
        </div>

    )

}

export default Route;
