import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {

  const [stationList, setStationList] = useState([])
  const [selectedStation, setSelectedStation] = useState(null)
  const [selectedStationName, setSelectedStationName] = useState(null)
  const [currentTrains, setCurrentTrains] = useState([])

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_RAIL_INFO_API}/jStations?api_key=${process.env.REACT_APP_SECRET}`)
      .then((data) => setStationList(data.data.Stations));
  }, [])



  function getCustomColor(line) {
    switch(line) {
      case 'RD':
        return 'red'
      case 'BL':
        return 'blue'
      case 'OR':
        return 'orange'
      case 'GR':
        return 'green'
      case 'SV':
        return 'gray'
      case 'YL':
        return '#ffcc00'
    }
  }

  function getTrainData(event) {
    console.log('calling api with station', event.target.value)
    axios.get(`${process.env.REACT_APP_PREDICTION_API}/${event.target.value}?api_key=${process.env.REACT_APP_SECRET}`)
      .then(payload => {
        payload.data.Trains.length && setSelectedStationName(payload.data.Trains[0].LocationName)
        setCurrentTrains(payload.data.Trains)

      })
  }

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) =>{
      console.log('lat', position.coords.latitude)
      console.log('long', position.coords.longitude);
    })
  }, [])

  const handleNearestStationClick = () => {

  }



  const handleStationSelect = (event) => {
    setSelectedStation(event.target.value)

    getTrainData(event)

  }



  function getTrainStatus(min){
    if (min === 'BRD'){
      return 'BOARDING NOW'
    }
    else if (min === 'ARR'){
      return 'ARRIVING'
    }
    else{
      return `${min} minute${(min > 1) ? 's' : ''} away`
    }

  }



  return (
    <div className="App">
      <div onClick={handleNearestStationClick}>Get Nearest Station</div>
      <div>or</div>
      <label htmlFor='stations'>Select Station: </label>
      {stationList.length && (
      <select name='stations' onChange={handleStationSelect}>
        {stationList.map((item) => {
          return(<option value={item.Code}>{item.Name}</option>)
        })}
      </select> )}
      {selectedStationName && <div>Trains leaving from {selectedStationName} </div>}
      <div >
        {currentTrains.map((item) => {
          return(
            <div className='wrapper'><b style={{color: getCustomColor(item.Line)}}>{item.DestinationName}</b> : {getTrainStatus(item.Min)} </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
