    const [startStation, setStartStation] = useState(null)
    const [endStation, setEndStation] = useState(null)
    const [stationList, setStationList] = useState([])




    useEffect(() => {
        axios.get(`${process.env.REACT_APP_ROUTE_API}/jPath?FromStationCode=${props.start}&ToStationCode=${props.end}&api_key=${process.env.REACT_APP_SECRET}`)
          .then((balh) => console.log(balh))
      }, [])

    useEffect(() => {
    axios.get(`${process.env.REACT_APP_RAIL_INFO_API}/jStations?api_key=${process.env.REACT_APP_SECRET}`)
