import React, { useEffect, useState, useContext } from 'react';
import { getDistance } from 'geolib';
import axios from 'axios';
import './App.css';

const Route = (props) => {

    const [currentLocation, setCurrentLocation] = useState({latitude: 38.924989, longitude: -77.052658})
    const [stationList, setStationList] = useState([])
    const [currentRoute, setCurrentRoute] = useState([])
    const [startStation, setStartStation] = useState(null)
    const [endStation, setEndStation] = useState(null)
    const [showResponse, setShowResponse] = useState(false)
    const [stationNames, setStationNames] = useState({})
    const [startDelta, setStartDelta] = useState(0)
    const [endDelta, setEndDelta] = useState(0)
    const [currentTrains, setCurrentTrains] = useState([])
    const [startLocation, setStartLocation] = useState([])
    const [endLocation, setEndLocation] = useState([])

    const tempCurrLoc = {latitude: 38.924989, longitude: -77.052658}
    const tempDestLoc = {latitude: 38.902736, longitude: -77.0329657}


    useEffect(() => {
        setStationList(props.stationList)
      }, [props.stationList])

    useEffect(() => {
        setStationNames(props.stationNames)
    }, [props.stationNames])


    useEffect(() => {
        if (startStation && endStation) {
            var start = startStation
            var end = endStation
            //console.log('fetching route data', start, end)
            if (start && end){
                axios.get(`${process.env.REACT_APP_ROUTE_API}?FromStationCode=${start}&ToStationCode=${end}&api_key=${process.env.REACT_APP_SECRET}`)
                    .then((data) => setCurrentRoute(data.data['StationToStationInfos']))
            }
        }
    }, [startStation, endStation])

    /*useEffect(() => {

            var stuff = currentRoute
            var stuff2 = [{"SourceStation":"A07","DestinationStation":"A13","CompositeMiles":8.23,"RailTime":16,"RailFare":{"PeakTime":3.85,"OffPeakTime":3.20,"SeniorDisabled":1.90}}]
            console.log('looking further', stuff[0])

    }, [currentRoute])*/

    function findNearestStation(inputLocation){
        var minDistance = Infinity
        var minStationCode = null
        stationList.map((station) => {
            var currDist = getDistance(
                                {latitude: station.Lat, longitude: station.Lon},
                                inputLocation) * 0.000621371
            if (currDist < minDistance){
                minDistance = currDist
                minStationCode = station.Code
            }
        })
        //console.log('min dist', minDistance)
        return [minDistance, minStationCode]
    }

    const handleDestinationEnter = () => {
        var starter = findNearestStation(currentLocation)
        var startDelta = starter[0]
        var startStation = starter[1]

        var ender = findNearestStation(tempDestLoc)
        var endDelta = ender[0]
        var endStation = ender[1]

        console.log(startStation, endStation)
        setStartStation(startStation)
        setEndStation(endStation)
        setShowResponse(true)
        //console.log(startDelta)
        //console.log(endDelta)
        setStartDelta(startDelta)
        setEndDelta(endDelta)
    }

    useEffect(() => {
        if (startStation){
            var value = startStation
            //console.log('calling api with station', value)
            axios.get(`${process.env.REACT_APP_PREDICTION_API}/${value}?api_key=${process.env.REACT_APP_SECRET}`)
              .then(payload => {
                payload.data.Trains.length && setCurrentTrains(payload.data.Trains)
                console.log(payload.data.Trains)
              })
          }



    }, [startStation])

    function generateResponse() {
        if (showResponse && currentRoute[0] && Object.keys(stationNames).length != 0){
            var start = stationNames[startStation]
            var end = stationNames[endStation]

            console.log(currentRoute[0])
            var routeData = currentRoute[0]

            return(
                <div className='spacer'>
                    <div className='double_bottom_space'><b>Recommended Route:</b></div>
                    <div className='bottom_space wrapper'><b>Depart from:</b> {start} ({startDelta.toFixed(2)} miles from you)</div>
                    <div className='bottom_space wrapper'><b>Get off at: </b>{end} ({endDelta.toFixed(2)} miles to destination)</div> 
                    <div className='row  double_bottom_space'>
                        <div className='wrapper'><b>Ride time: </b>{routeData.RailTime} minutes</div>
                        <div className='wrapper'><b>Ride cost: </b>${routeData.RailFare.OffPeakTime}</div>
                    </div>

                    <div>
                    <div className='double_bottom_space'><b>Trains leaving from: {stationNames[startStation]}</b></div>

          {(currentTrains.length > 0) ? 
            currentTrains.map((item) => {
              return(
                <div className='wrapper row vertical_center'>
                  <div style={{backgroundColor: getCustomColor(item.Line)}} className='spaceRight' id='lineIcon'></div>
                  <b>{item.DestinationName}</b> : {getTrainStatus(item.Min)}
                </div>
              )}): <div className='spacer'>No trains found</div>}
        </div>
                </div>




            )
        }
    }


      function getCustomColor(line) {
        switch(line) {
          case 'RD':
            return '#f76f6f'
          case 'BL':
            return '#8fc1ff'
          case 'OR':
            return '#8fc1ff'
          case 'GR':
            return '#abd1af'
          case 'SV':
            return '#c2c2c2'
          case 'YL':
            return '#ffe999'
        }
      }

      function getTrainStatus(min){
        if (min === 'BRD'){
          return <b> BOARDING NOW</b>
        }
        else if (min === 'ARR'){
          return <b> ARRIVING NOW</b>
        }
        else{
          return `${min} minute${(min > 1) ? 's' : ''} away`
        }

      }



      var divItems = document.getElementsByClassName("button");

      function handleStartClick(item, code){
        console.log('blah')
        var object = document.getElementById(code + 'Start')
        if (object){
            clear();
            document.getElementById('currStart').style.backgroundColor = 'red';
        }


        function clear() {
            for(var i=0; i < divItems.length; i++) {
                var item = divItems[i];
                item.style.backgroundColor = 'white';
            }
        }

      }

      function handleEndClick(code){

      }


    return(
        <div>
            <div className='column spacer'>

                <div>Where are you starting?</div>
                <div className='row'>
                    <div className='wrapper button smallButtonWidth' id='currStart' onClick={() => handleStartClick(this, 'curr')}>Current Location</div>
                    <div className='wrapper button smallButtonWidth' id='wpStart' onClick={() => handleStartClick(this, 'wp')}>The Washington Post</div>
                    <div className='wrapper button smallButtonWidth' onClick={() => handleStartClick(this, 'curr')}></div>
                    <div className='wrapper button smallButtonWidth' onClick={() => handleStartClick(this, 'curr')}></div>

                </div>
                <div>Where are you going?</div>
                <div className='row'>
                    <div className='wrapper button smallButtonWidth' id='currEnd' onClick={() => handleEndClick(this, 'curr')}>Current Location</div>
                    <div className='wrapper button smallButtonWidth' id='wpEnd' onClick={() => handleEndClick('this', 'wp')}>The Washington Post</div>
                    <div className='wrapper button smallButtonWidth' onClick={() => handleEndClick(this, 'curr')}></div>
                    <div className='wrapper button smallButtonWidth' onClick={() => handleEndClick(this, 'curr')}></div>
                </div>



                <div className='button largeButtonWidth spacer' onClick={handleDestinationEnter}>entered destination</div>

                {generateResponse()}




            </div>           
        </div>

    )

}

export default Route;
