import React, { useEffect, useState, createContext, useReducer } from 'react';
import { getDistance } from 'geolib';
import axios from 'axios';
import Route from './route.js'
import Tracker from './tracker.js'
import './App.css';

export const AppContext = createContext();


const App = () => {


  const [view, setView] = useState('plan')
  const [stationList, setStationList] = useState([])
  const [currentLocation, setCurrentLocation] = useState(null)
  const [stationNames, setStationNames] = useState({})



  const handleViewToggle = () => {
    if(document.getElementById('track').clicked){
      console.log('blah')
    }
  }

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_RAIL_INFO_API}/jStations?api_key=${process.env.REACT_APP_SECRET}`)
        .then((data) => setStationList(data.data.Stations));

    }, [])

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) =>{
      setCurrentLocation({latitude: position.coords.latitude, longitude: position.coords.longitude})
    })
  }, [])

  useEffect(() => {
    var temp_dict = {}
    if (stationList.length){
      stationList.map((station) => {
        temp_dict[station.Code] = station.Name
      })

      setStationNames(temp_dict)
    }
  }, [stationList])






  return (
    <div className="App row">
      <div className=''>
        <div className='spacer title noBottom'>WMATA Live Train Tracker</div>
        <div className='spacer column noBottom'>


          <div className='bottom_space'>
            <input checked type="radio" id="track" name="view" value="track" onChange={handleViewToggle}></input>
            <label className=' space_left' htmlFor='track'>Train Tracker</label>
          </div>
          <div className='bottom_space'>
            <input className='space_right' type="radio" id="plan" name="view" value="plan" onChange={handleViewToggle}></input>
            <label className='space_left' htmlFor='plan'>Trip Planner </label>
          </div>
        </div>

        {view === 'plan' ? <div>tracker</div> : <div>planner</div>}

        <Route 
          stationList={stationList}
          currentLocation={currentLocation}
          start={'A06'}
          end={'A09'}
          stationNames={stationNames}>
        </Route>

        <Tracker stationList={stationList} currentLocation={currentLocation}></Tracker>

      </div>

    </div>
  );
}

export default App;
