import React, { useEffect, useState } from 'react';
import { getDistance } from 'geolib';
import axios from 'axios';
import './App.css';

const Route = props => {

    const [startStation, setStartStation] = useState(null)
    const [endStation, setEndStation] = useState(null)
    const [startStationName, setStartStationName] = useState(null)
    const [endStationName, setEndStationName] = useState(null)
    const [stationList, setStationList] = useState([])




    useEffect(() => {
    axios.get(`${process.env.REACT_APP_RAIL_INFO_API}/jStations?api_key=${process.env.REACT_APP_SECRET}`)
        .then((data) => setStationList(data.data.Stations));
    }, [])

    const fetchRouteData = () => {

        if (startStation && endStation){
            axios.get(`${process.env.REACT_APP_ROUTE_API}/jPath?FromStationCode=${startStation}&ToStationCode=${endStation}&api_key=${process.env.REACT_APP_SECRET}`)
                .then((data) => console.log(data))
        }
    }

    const handleStartSelect = (event) => {stationList.map((station) => {
        if (station.Code === event.target.value){
            setStartStationName(station.Name)
        }
        })
        setStartStation(event.target.value)
        fetchRouteData()
        }

    const handleEndSelect = (event) => {stationList.map((station) => {
        if (station.Code === event.target.value){
            setEndStationName(station.Name)
        }
        })
        setEndStation(event.target.value)
        fetchRouteData
        }

    return(
        <div>
            <div>this will be the route guide</div>
            <div className='column spacer'>
                <label className='bottom_space' htmlFor='start'>Select Start Station: </label>
                <label className='bottom_space' htmlFor='start'>Select Station: </label>



                {stationList.length && (
                <div>
                <select name='start' id='stationSelect' onChange={handleStartSelect}>
                    <option selected disabled hidden style={{display: 'none'}} value=''></option>
                    {stationList.map((item) => {
                        return(<option key={item.Code} value={item.Code}>{item.Name}</option>)
                    })}
                </select> 

                <select name='end' id='stationSelect' onChange={handleEndSelect}>
                    <option selected disabled hidden style={{display: 'none'}} value=''></option>
                    {stationList.map((item) => {
                        return(<option key={item.Code} value={item.Code}>{item.Name}</option>)
                    })}
                </select> 
                </div>
                )


                }
                {startStationName && <div>{startStationName}</div>}
                {endStationName && <div>{endStationName}</div>}

            </div>           
        </div>

    )

}

export default Route;
