import React, { useEffect, useState } from 'react';
import { getDistance } from 'geolib';
import axios from 'axios';
import './App.css';

const App = () => {

  const [stationList, setStationList] = useState([])
  const [selectedStation, setSelectedStation] = useState(null)
  const [selectedStationName, setSelectedStationName] = useState(null)
  const [currentTrains, setCurrentTrains] = useState([])
  const [currentLocation, setCurrentLocation] = useState({})

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_RAIL_INFO_API}/jStations?api_key=${process.env.REACT_APP_SECRET}`)
      .then((data) => setStationList(data.data.Stations));
  }, [])



  function getCustomColor(line) {
    switch(line) {
      case 'RD':
        return 'red'
      case 'BL':
        return 'blue'
      case 'OR':
        return 'orange'
      case 'GR':
        return 'green'
      case 'SV':
        return 'gray'
      case 'YL':
        return '#ffcc00'
    }
  }



  function getTrainData(event) {
    console.log('calling api with station', event.target.value)
    axios.get(`${process.env.REACT_APP_PREDICTION_API}/${event.target.value}?api_key=${process.env.REACT_APP_SECRET}`)
      .then(payload => {
        payload.data.Trains.length && setSelectedStationName(payload.data.Trains[0].LocationName)
        setCurrentTrains(payload.data.Trains)

      })
  }


  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) =>{

      setCurrentLocation({latitude: position.coords.latitude, longitude: position.coords.longitude})

    })
  }, [])

  const handleNearestStationClick = () => {
    var minDistance = Infinity
    var closestStation = null

    stationList.map((station) =>{
        var currDistance = getDistance(
                        {currentLocation}.currentLocation, {
                        latitude: station.Lat,
                        longitude: station.Lon
                      })

        if (currDistance < minDistance){
          minDistance = currDistance
          closestStation = station.Code
        }
    })
    document.getElementById('stationSelect').value=closestStation
    handleStationSelect
    console.log('the closest station is', closestStation, 'at distance', minDistance)

  }



  const handleStationSelect = (event) => {
    setSelectedStation(event.target.value)
    getTrainData(event)
  }

  function getTrainStatus(min){
    if (min === 'BRD'){
      return 'BOARDING NOW'
    }
    else if (min === 'ARR'){
      return 'ARRIVING'
    }
    else{
      return `${min} minute${(min > 1) ? 's' : ''} away`
    }

  }



  return (
    <div className="App">
      <div className='button' onClick={handleNearestStationClick}>Get Nearest Station</div>
      <div>or</div>
      {currentLocation && <div>{currentLocation.longitude}</div>}
      <label htmlFor='stations'>Select Station: </label>
      {stationList.length && (
      <select name='stations' id='stationSelect' onChange={handleStationSelect}>
        {stationList.map((item) => {
          return(<option key={item.Code} value={item.Code}>{item.Name}</option>)
        })}
      </select> )}
      {selectedStationName && <div>Trains leaving from {selectedStationName} </div>}
      <div >
        {currentTrains.map((item) => {
          return(
            <div className='wrapper'><b style={{color: getCustomColor(item.Line)}}>{item.DestinationName}</b> : {getTrainStatus(item.Min)} </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
