import React, { useEffect, useState } from 'react';
import { getDistance } from 'geolib';
import axios from 'axios';
import Route from './route.js'
import Tracker from './tracker.js'
import './App.css';

const App = () => {

  const [view, setView] = useState('plan')
  const [currentLocation, setCurrentLocation] = useState({})





  const handleViewToggle = () => {
    if(document.getElementById('track').clicked){
      console.log('blah')
    }
  }

  useEffect(() => {
    navigator.geolocation.getCurrentPosition((position) =>{
      setCurrentLocation({latitude: position.coords.latitude, longitude: position.coords.longitude})
    })
  }, [])

  console.log(currentLocation)



  return (
    <div className="App row">
      <div className=''>
        <div className='spacer title noBottom'>WMATA Live Train Tracker</div>
        <div className='spacer column noBottom'>


          <div className='bottom_space'>
            <input checked type="radio" id="track" name="view" value="track" onChange={handleViewToggle}></input>
            <label className=' space_left' htmlFor='track'>Train Tracker</label>
          </div>
          <div className='bottom_space'>
            <input className='space_right' type="radio" id="plan" name="view" value="plan" onChange={handleViewToggle}></input>
            <label className='space_left' htmlFor='plan'>Trip Planner </label>
          </div>
        </div>

        {view === 'plan' ? <div>tracker</div> : <div>planner</div>}

        <Route start={'A06'} end={'A09'}></Route>

        <Tracker currentLocation={currentLocation}></Tracker>


      </div>

    </div>
  );
}

export default App;
